mapper.select:
select 

from applyfile
where sequenceNumber = #{sequencenumber,jdbcType=VARCHAR}
  and timeLimit = #{timelimit,jdbcType=DATE}

mapper.delete:
delete from applyfile
where sequenceNumber = #{sequencenumber,jdbcType=VARCHAR}
  and timeLimit = #{timelimit,jdbcType=DATE}

mapper.insert:
insert into applyfile (sequenceNumber, timeLimit, time, 
  sourcePerson, sourceTitle, instructions, 
  instructior, direction, comment
  )
values (#{sequencenumber,jdbcType=VARCHAR}, #{timelimit,jdbcType=DATE}, #{time,jdbcType=TIMESTAMP}, 
  #{sourceperson,jdbcType=VARCHAR}, #{sourcetitle,jdbcType=VARCHAR}, #{instructions,jdbcType=VARCHAR}, 
  #{instructior,jdbcType=VARCHAR}, #{direction,jdbcType=VARCHAR}, #{comment,jdbcType=VARCHAR}
  )

mapper.update:
update applyfile
set time = #{time,jdbcType=TIMESTAMP},
  sourcePerson = #{sourceperson,jdbcType=VARCHAR},
  sourceTitle = #{sourcetitle,jdbcType=VARCHAR},
  instructions = #{instructions,jdbcType=VARCHAR},
  instructior = #{instructior,jdbcType=VARCHAR},
  direction = #{direction,jdbcType=VARCHAR},
  comment = #{comment,jdbcType=VARCHAR}
where sequenceNumber = #{sequencenumber,jdbcType=VARCHAR}
  and timeLimit = #{timelimit,jdbcType=DATE}
